mapper.select:
select 

from BLACK_IP where 1 = 1

and ( ID like '${searchValue}%')


order by ${orderColumn}
	${orderDir}


limit ${start},${length}

mapper.insert:
insert into BLACK_IP (ID, IP, IS_BANNED, 
  BANNED_TIME, DESCRIPTION, CREATE_TIME
  )
values (#{id,jdbcType=INTEGER}, #{ip,jdbcType=INTEGER}, #{isBanned,jdbcType=VARCHAR}, 
  #{bannedTime,jdbcType=INTEGER}, #{description,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}
  )

    replace into BLACK_IP (ID, IP, IS_BANNED, 
      BANNED_TIME, DESCRIPTION, CREATE_TIME
      )
    values 

		(#{item.id,jdbcType=INTEGER}, #{item.ip,jdbcType=INTEGER}, #{item.isBanned,jdbcType=VARCHAR}, 
      #{item.bannedTime,jdbcType=INTEGER}, #{item.description,jdbcType=VARCHAR}, #{item.createTime,jdbcType=TIMESTAMP}
      )
