mapper.select:
select 

from GM_USER where 1 = 1

and ( ID like '${searchValue}%' or `NAME` like '${searchValue}%' or `USER_NAME` like '${searchValue}%' )


order by ${orderColumn}
	${orderDir}


limit ${start},${length}

