mapper.select:
select 

from SYS_CHEST c, SYS_ITEM i where c.SYS_ITEM_ID = i.ID

and ( c.ID like '${searchValue}%' or c.SYS_ITEM_ID like '${searchValue}%' or i.`NAME` like '${searchValue}%' or i.DISPLAY_NAME like '${searchValue}%')


order by c.${orderColumn}
	${orderDir}


limit ${start},${length}

mapper.insert:
insert into SYS_CHEST (ID, TYPE, LEVEL, 
  SYS_ITEM_ID, WEIGHT, NUMBER, 
  USE_TYPE, IS_NOTICE, PRICE, 
  CHIP_PRICE, IS_DELETED, WEIGHT1
  )
values (#{id,jdbcType=INTEGER}, #{type,jdbcType=INTEGER}, #{level,jdbcType=INTEGER}, 
  #{sysItemId,jdbcType=INTEGER}, #{weight,jdbcType=INTEGER}, #{number,jdbcType=INTEGER}, 
  #{useType,jdbcType=INTEGER}, #{isNotice,jdbcType=INTEGER}, #{price,jdbcType=INTEGER}, 
  #{chipPrice,jdbcType=INTEGER}, #{isDeleted,jdbcType=BIT}, #{weight1,jdbcType=INTEGER}
  )
