mapper.select:
select 

from SYS_ITEM_GUN_PROPERTY where IS_DELETED = 'N'

and ( ID like '${searchValue}%' )


order by ${orderColumn}
	${orderDir}


limit ${start},${length}

mapper.insert:
insert into SYS_ITEM_GUN_PROPERTY (ID, `INDEX`, VALUE, 
  VALUE2, TIME, IS_DELETED, MULTI_TYPE, DESC_I18N, REMARK
  )
values (#{id,jdbcType=INTEGER}, #{index,jdbcType=INTEGER}, #{value,jdbcType=INTEGER}, 
  #{value2,jdbcType=INTEGER}, #{time,jdbcType=INTEGER}, #{isDeleted,jdbcType=VARCHAR}
  , #{multiType,jdbcType=LONGVARCHAR}, #{descI18n,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}
  )

    replace into SYS_ITEM_GUN_PROPERTY (ID, `INDEX`, VALUE, 
      VALUE2, TIME, IS_DELETED, MULTI_TYPE, DESC_I18N, REMARK
      )
    values 

	    (#{item.id,jdbcType=INTEGER}, #{item.index,jdbcType=INTEGER}, #{item.value,jdbcType=INTEGER}, 
	      #{item.value2,jdbcType=INTEGER}, #{item.time,jdbcType=INTEGER}, #{item.isDeleted,jdbcType=VARCHAR}, 
	      #{item.multiType,jdbcType=LONGVARCHAR}, #{item.descI18n,jdbcType=VARCHAR}, #{item.remark,jdbcType=VARCHAR}
	    )
