mapper.select:
select 

from SYS_ITEM where IS_DELETED = 'N'

and ( ID like '${searchValue}%' or DISPLAY_NAME like '%${searchValue}%' or `NAME` like '%${searchValue}%' )



	and `TYPE` = ${specialSearch.type}


	and `SUB_TYPE` = ${specialSearch.subType}



order by ${orderColumn}
	${orderDir}


limit ${start},${length}

	select count(*) from SYS_ITEM where IS_DELETED = 'N'

  and ( ID like '${searchValue}%' or DISPLAY_NAME like '%${searchValue}%' or `NAME` like '%${searchValue}%' )



  	and `TYPE` = ${specialSearch.type}


  	and `SUB_TYPE` = ${specialSearch.subType}
