mapper.select:
select 

from SYS_ITEM_PRICE pri left join SYS_ITEM sitem  on pri.SYS_ITEM_ID = sitem.ID
where pri.IS_DELETED = 'N'

and ( pri.SYS_ITEM_ID like '${searchValue}%' or pri.PAY_GROUP like '${searchValue}%' or sitem.DISPLAY_NAME like '%${searchValue}%' )


order by ${orderColumn}
	${orderDir}


limit ${start},${length}
