mapper.select:
select 

from SYS_NOTICE where IS_DELETED = 'N'

and ( ID like '${searchValue}%' or p.`CONTENT` like '%${searchValue}%'  )


order by ${orderColumn}
	${orderDir}


limit ${start},${length}

mapper.insert:
insert into SYS_NOTICE (ID, TYPE, CONTENT, 
  START_TIME, END_TIME, NOTICE_TIME, 
  IS_DELETED, CREATE_TIME, UPDATE_TIME
  )
values (#{id,jdbcType=INTEGER}, #{type,jdbcType=INTEGER}, #{content,jdbcType=VARCHAR}, 
  #{startTime,jdbcType=TIMESTAMP}, #{endTime,jdbcType=TIMESTAMP}, #{noticeTime,jdbcType=INTEGER}, 
  #{isDeleted,jdbcType=CHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

    replace into SYS_NOTICE (ID, TYPE, CONTENT, 
      START_TIME, END_TIME, NOTICE_TIME, 
      IS_DELETED, CREATE_TIME, UPDATE_TIME
      )
    values 

	    (#{item.id,jdbcType=INTEGER}, #{item.type,jdbcType=INTEGER}, #{item.content,jdbcType=VARCHAR}, 
	    #{item.startTime,jdbcType=TIMESTAMP}, #{item.endTime,jdbcType=TIMESTAMP},#{item.noticeTime,jdbcType=INTEGER}, 
      	#{item.isDeleted,jdbcType=CHAR}, #{item.createTime,jdbcType=TIMESTAMP}, #{item.updateTime,jdbcType=TIMESTAMP}
	    )
