mapper.select:
select 

from SYS_TEAM_BUFF tb,SYS_ITEM sitem 
where tb.IS_DELETE = 0 and tb.SYS_ITEM_ID= sitem.ID

and ( tb.SYS_ITEM_ID like '${searchValue}%' or sitem.DISPLAY_NAME like '%${searchValue}%' )


order by ${orderColumn}
	${orderDir}


limit ${start},${length}

mapper.insert:
insert into SYS_TEAM_BUFF (ID, RANK, SYS_ITEM_ID, 
  UNIT, UNIT_TYPE, IS_DELETE, 
  IS_ENABLE, PAY_TYPE, COST
  )
values (#{id,jdbcType=INTEGER}, #{rank,jdbcType=INTEGER}, #{sysItemId,jdbcType=INTEGER}, 
  #{unit,jdbcType=INTEGER}, #{unitType,jdbcType=TINYINT}, #{isDelete,jdbcType=BIT}, 
  #{isEnable,jdbcType=BIT}, #{payType,jdbcType=TINYINT}, #{cost,jdbcType=INTEGER}
  )

    replace into SYS_TEAM_BUFF (ID, RANK, SYS_ITEM_ID, 
      UNIT, UNIT_TYPE, IS_DELETE, 
      IS_ENABLE, PAY_TYPE, COST
      )
    values 

	    (#{item.id,jdbcType=INTEGER}, #{item.rank,jdbcType=INTEGER}, #{item.sysItemId,jdbcType=INTEGER}, 
      #{item.unit,jdbcType=INTEGER}, #{item.unitType,jdbcType=TINYINT}, #{item.isDelete,jdbcType=BIT}, 
      #{item.isEnable,jdbcType=BIT}, #{item.payType,jdbcType=TINYINT}, #{item.cost,jdbcType=INTEGER}
      )
