mapper.select:
select 

from UNSPEAKER u,PLAYER p where u.PLAYER_ID = p.ID

and ( p.ID like '${searchValue}%' or p.`NAME` like '${searchValue}%' or p.`USER_NAME` like '${searchValue}%' )


order by u.${orderColumn}
	${orderDir}


limit ${start},${length}

mapper.insert:
insert into UNSPEAKER (ID, PLAYER_ID, CREATE_TIME
  )
values (#{id,jdbcType=INTEGER}, #{playerId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}
  )

    replace into UNSPEAKER (ID, PLAYER_ID, CREATE_TIME)
    values 

	    (#{item.id,jdbcType=INTEGER}, #{item.playerId,jdbcType=INTEGER}, #{item.createTime,jdbcType=TIMESTAMP}
	    )

mapper.update:
update UNSPEAKER
set PLAYER_ID = #{playerId,jdbcType=INTEGER},
  CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}
where ID = #{id,jdbcType=INTEGER}
