mapper.select:
select 

from PLAYER_PACK${tableSuffix}
where ID = #{id,jdbcType=INTEGER}
  and SYS_CHARACTER_ID = #{sysCharacterId,jdbcType=INTEGER}

mapper.delete:
delete from PLAYER_PACK${tableSuffix}
where ID = #{id,jdbcType=INTEGER}
  and SYS_CHARACTER_ID = #{sysCharacterId,jdbcType=INTEGER}

mapper.insert:
insert into PLAYER_PACK${tableSuffix} (ID, SYS_CHARACTER_ID, PLAYER_ID, 
  PLAYER_ITEM_ID, TYPE, PACK_ID, 
  SEQ, EXPIRE_TIME, BACK_UP
  )
values (#{id,jdbcType=INTEGER}, #{sysCharacterId,jdbcType=INTEGER}, #{playerId,jdbcType=INTEGER}, 
  #{playerItemId,jdbcType=INTEGER}, #{type,jdbcType=CHAR}, #{packId,jdbcType=SMALLINT}, 
  #{seq,jdbcType=SMALLINT}, #{expireTime,jdbcType=TIMESTAMP}, #{backUp,jdbcType=VARCHAR}
  )

mapper.update:
update PLAYER_PACK${tableSuffix}


    PLAYER_ID = #{playerId,jdbcType=INTEGER},


    PLAYER_ITEM_ID = #{playerItemId,jdbcType=INTEGER},


    TYPE = #{type,jdbcType=CHAR},


    PACK_ID = #{packId,jdbcType=SMALLINT},


    SEQ = #{seq,jdbcType=SMALLINT},


    EXPIRE_TIME = #{expireTime,jdbcType=TIMESTAMP},


    BACK_UP = #{backUp,jdbcType=VARCHAR},


where ID = #{id,jdbcType=INTEGER}
  and SYS_CHARACTER_ID = #{sysCharacterId,jdbcType=INTEGER}

update PLAYER_PACK${tableSuffix}
set PLAYER_ID = #{playerId,jdbcType=INTEGER},
  PLAYER_ITEM_ID = #{playerItemId,jdbcType=INTEGER},
  TYPE = #{type,jdbcType=CHAR},
  PACK_ID = #{packId,jdbcType=SMALLINT},
  SEQ = #{seq,jdbcType=SMALLINT},
  EXPIRE_TIME = #{expireTime,jdbcType=TIMESTAMP},
  BACK_UP = #{backUp,jdbcType=VARCHAR}
where ID = #{id,jdbcType=INTEGER}
  and SYS_CHARACTER_ID = #{sysCharacterId,jdbcType=INTEGER}
